    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        String enc="";
        String plainTxt=inputTxt.getText();
        String alg=algorithm.getSelectedItem().toString();
        switch(alg.charAt(0)){
             case 'C':{
                enc=CaesarCipher.Encrypt(plainTxt,Integer.parseInt(key.getText()));
                break;
            }
            case 'P':{
                enc=PlayFair.Encrypt(plainTxt,key.getText());
                break;
            }
            case 'D':{
                enc=DES.Encrypt(plainTxt, key.getText());
                break;
            }
            case 'F':{
                char op =OP.getSelectedItem().toString().charAt(0);
                enc=Feistel.Encrypt(plainTxt, key.getText(),op);
                break;
            }
            case 'R':{
                enc=RC4.Encrypt(plainTxt, key.getText());
                break;
            }
        }
        output.setText(enc);
    }//GEN-LAST:event_jButton1ActionPerformed

