    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
       
        String dec="";
        String cipherTxt=inputTxt.getText();
        String alg=algorithm.getSelectedItem().toString();
        int keyy=0;
        switch(alg.charAt(0)){
             case 'C':{
                dec=CaesarCipher.Decrypt(cipherTxt,Integer.parseInt(key.getText()));
                break;
            }
            case 'P':{
                dec=PlayFair.Decrypt(cipherTxt,key.getText());
                break;
            }
            case 'D':{
                dec=DES.Decrypt(cipherTxt, key.getText());
                break;
            }
            case 'F':{
                char op = OP.getSelectedItem().toString().charAt(0);
                dec=Feistel.Decrypt(cipherTxt, key.getText(),op);
                break;
            }
            case 'R':{
                dec=RC4.Encrypt(cipherTxt, key.getText());
                break;
            }
        }
        output.setText(dec);
    }//GEN-LAST:event_jButton3ActionPerformed

